import { Redirect } from "react-router-dom";


import DataAPI from '../../../helpers/DataAPI.js'
import {getCookie} from '../../../helpers/sessionCookie.js'

import React, {useState} from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardGroup,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CLink,
  CRow
} from '@coreui/react'
import CIcon from '@coreui/icons-react'


const ResetPassword = () => {

  const [email, setEmail] = useState('');
  const [emailSent, setEmailSent] = useState(false);
  const [message, setMessage] = useState('');
  
  const authenticated = getCookie('name')!==false && getCookie('name')!=='';


  const sendResetPassword = () => {

    DataAPI({
      'endpoint': 'security/authenticate/reset',
      'method' : 'POST',
      'body': {
          "id": email,
          "newPassword": email,
          "newPasswordConfirm": email
      },
    }).then(
      response => {

        if (response.received) {          
          


          // window.location.reload();
        } else {
          alert('An error occurred!');
        }

      }
    ).catch(
      e=> { alert('An error occurred!') }
    )
  }

  return (


    authenticated ?

      <Redirect to={'/'} />:

  
    
    <div className="bg-primary c-app c-default-layout flex-row align-items-center">
      <CContainer>
        <CRow className="justify-content-center">
          <CCol md="6">
            <CCard className="p-4 border-light">
              <CCardBody>
                {!emailSent ?
                  <CForm>
                    <h1 className="text-dark-blue">Reset Password</h1>
                    <p className="text-muted">Enter your e-mail address</p>
                    <CInputGroup className="mb-3">
                      <CInputGroupPrepend>
                        <CInputGroupText>
                          <CIcon name="cil-envelope-closed" />
                        </CInputGroupText>
                      </CInputGroupPrepend>
                      <CInput onChange={(ev) => {setEmail(ev.target.value)}} value={email} type="text" placeholder="E-mail" autoComplete="username" />
                    </CInputGroup>
                    <CRow>
                      <CCol xs="12">
                        <CButton onClick={sendResetPassword} color="primary" className="px-4 mr-3">Send</CButton>
                      </CCol>
                    </CRow>
                  </CForm>
                  :
                  {message}
                }

              </CCardBody>
            </CCard>
          </CCol>
        </CRow>
      </CContainer>
    </div>
    
  )


}

export default ResetPassword
