  }

  const refreshChart = (responseData) => {

    setLoading(true)
    const tableData = []
    const commentChanged = []

    responseData.forEach(
      (row,index) => {

        let allGenerators;

        if (row.generators) {
          allGenerators = row.generators.map(generator => generator.code).join(', ');
        } else {
          allGenerators = '-';
        }

        const serviceOrder =  row.name.split('_').pop();

        const startDateEndDate = [];

        if (row.from) startDateEndDate.push(formatDate(row.from));
        if (row.to) startDateEndDate.push(formatDate(row.to));
        

        tableData.push({
          serviceOrder: '#'+serviceOrder,
          turbineId: allGenerators,
          startDateEndDate: startDateEndDate.join('-'),
          issue: row.name,
          status: row.isOpen ? 'open' : 'closed',
          comment: row.observations,
          download: process.env.REACT_APP_API_URL+row.downloadLink.replace('api/rest/', ''),
        })

        commentChanged[index] = false

      }
    )

    setTableData(tableData)
    setLoading(false)
  }


  const toggleStatus = (status, index) => {
    // let newStatuses = statuses
    // newStatuses[index] = 'Open'
    // setStatuses(newStatuses)
    const body = {}
    body.id = data[index].id;
    body.observations = "con comentarios";
    body.isOpen = status == 'open';
    
    DataAPI(
      {
      'endpoint': 'admin/documents',
      'method': 'PUT',
      'body': body
    })
      .then(function (responseData) {

        if (responseData.error && responseData.error.message) {
          setDataLoadError(true)
          alert(responseData.error.message)
        } 

        let new_data = data
        new_data[index].isOpen = status=='open'
        setData(new_data)
        refreshChart(new_data)

      })
  }





