          <CRow>
            <CCol sm="4">
              <h3 id="traffic" className="card-title mb-0">
                Maintenance
              </h3>
              {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
            </CCol>
            
            <CCol sm="8" className="text-right d-flex flex-center flex-justify-end">
              <h6 className="mr-2 ml-2 ml-xl-3 mb-0" style={{lineHeight:2.4,minWidth:'64px'}}>Turbine</h6>
              <CSelect className={'input-md'} disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); }} custom name="generator" id="generator">
                <option value="0">All turbines</option>
              {generators.map((oGenerator) => (
                                  <option value={oGenerator.id}>
                                    {oGenerator.code}
                                  </option>
                                ))}</CSelect>
              <h6 className="mr-2 ml-2 ml-xl-3 m-0" style={{lineHeight:2.4}}>Status</h6>
              <CSelect className={'input-sm'} disabled={loading} onChange={(ev) => {setStatus(ev.target.value); }} custom name="status" id="status">
                <option value="null">All</option>
                <option value="open">Open</option>
                <option value="closed">Closed</option>
              </CSelect>
              <h6 className="mr-2 ml-2 ml-xl-3 m-0" style={{lineHeight:2.4}}>Period</h6>
              <DateFilter value={dateRange} options={['y','cm','cy','x','xx']} disabled={loading} onChange={(value) => { setDateRange(value);}} />
              <h6 className="mr-2 ml-2 ml-xl-3 m-0" style={{lineHeight:2.4,minWidth:'75px'}} >Search term</h6>
              <CInput type="text" placeholder="" autoComplete="username" style={{minWidth:'100px'}} disabled={loading} onChange={(ev) => { setTerm(ev.target.value);}} />
              <CButton color="primary" disabled={loading} className="mr-2 ml-3" onClick={() => { fetchData();}} >Submit</CButton>
            </CCol>
          </CRow>






        </CCardHeader>
      <CCardBody>
        {dataLoaded || dataLoadError ? 
          <CDataTable
              items={tableData}
              fields={fields}
              striped
              itemsPerPage={50}
              pagination
              scopedSlots = {{
                'download':
                  (item, index)=>{
                    return (
                      <td className="py-2">
                        <a href={item.download}>DOWNLOAD</a>
                      </td>
                      )
                  },
                'status':
                (item, index)=>{
                  return (
                      <td className="py-1">
                        <CSelect
                          color="primary"
                          size="md"
                          className={"select-status input-sm "+item.status}
                          value={item.status}
                          onChange={(ev)=>{toggleStatus(ev.target.value, index)}}
                        >
                          <option key={'status-'+index+'-open'} value='open'>Open</option>
                          <option key={'status-'+index+'-closed'} value='closed'>Closed</option>
                        </CSelect>
                      </td>
                      )
                  },
                'comment':
                (item, index)=>{
                  return (
                      <td className="py-1">
                        <CInput
                          color="primary"
                          size="md"
                          className={"input-md"}
                          defaultValue={item.comment}
                          onChange={(ev)=>{toggleStatus(ev.target.value, index)}}
                        >
                        </CInput>
                        <CButton 
                          hidden={commentChanged[index]}
                          size="sm"
                          color="primary" 
                          className="btn-comment"
                        >
                          Save
                        </CButton>
                      </td>
                      )
                  },
              }}
            />
        : 
          <ChartLoading/>
        }

        {/* <Line data={graphData} /> */}

      </CCardBody>
    </CCard>
  )
}

