import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CSelect
} from '@coreui/react'
import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'
import { DocsLink } from 'src/reusable'

import DataAPI from '../../../helpers/DataAPI.js'

import {DateFilter, getDateLabel, formatDate} from '../../../helpers/utils.js'
import {getCookie} from '../../../helpers/sessionCookie.js'

const Charts = () => {

  const [dateRange, setDateRange] = useState('12w');
  const [groupBy, setGroupBy] = useState('week');
  const [loading, setLoading] = useState(false);

  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });

  useEffect(() => {
      
      refreshChart({period: dateRange, groupBy: groupBy});

  }, []);


  const fetchData = () => {

    setLoading(true);

    const body = {}
    body.location = getCookie('location');
    if (dateRange && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy

    DataAPI(
      {
      'endpoint': 'chart/curtailments',
      'body': body
    }).then(function (responseData) {


      setLoading(false);

      if (responseData.error) {
           if (responseData.error.message) {
            return(alert(responseData.error.message))
           } else {
            return(alert(responseData.error)) 
           }
        }


        const data = {
          labels: [],
          datasets: []
        }
        responseData.dataSet.forEach((rDs) => {
          const datasets = {
            label: rDs.label,
            backgroundColor: rDs.id === 1 ? '#f87979' : '#0400ff',
            data: []
          };
          responseData.data.forEach((rD) => {
            const dsData = rD.dataSet.find((dS) => dS.id === rDs.id);
            const value = (Math.round(dsData.value * 100) / 100).toFixed(2);
            datasets.data.push(value);
          });
          data.datasets.push(datasets);
        });

        responseData.data.forEach((rD) => {
          const rDfrom = rD.from;
          const rDto = rD.to;
          // data.labels.push(rDfrom.replace(" 00:00:00", "") + ' / ' + rDto.replace(" 00:00:00", ""));
          const label = (groupBy == "day") ? `${formatDate(rDfrom)}` : `${formatDate(rDfrom)} - ${formatDate(rDto)}`
          data.labels.push(label);

        });
        setGraphData(data)
      });


  }

  return (
    <CCard>
      <CCardHeader>
          <CRow>
            <CCol sm="7">
              <h3 id="traffic" className="card-title mb-0">
                Curtailments
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
              </CCol>
            
              <CCol sm="5" className="text-right d-flex flex-center">
                <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'60px'}}>Group by</h6>
                <CSelect value={groupBy} disabled={loading} onChange={(ev) => { setGroupBy(ev.target.value); }} custom name="groupBy" id="groupBy">
                    <option value="day">Day</option>
                    <option value="week" selected>Week</option>
                    <option value="month">Month</option>
                </CSelect>
                <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Period</h6>
                <DateFilter value={dateRange} options={['y','cm','cy','12w','12m','x']} disabled={loading} onChange={(value) => { setDateRange(value); }} />
                <CButton color="primary" disabled={loading} className="mr-2 ml-3" onClick={() => { fetchData();}} >Submit</CButton>
            </CCol>
                            </CRow>


        </CCardHeader>
      <CCardBody>
        MWh
        <CChartBar
          datasets={graphData.datasets}
          labels={graphData.labels}
          options={{
            tooltips: {
              enabled: true
            }
          }}
        />
        <p class="text-center">Week</p>
      </CCardBody>
    </CCard>
  )
}

export default Charts
