import React, { useState, useEffect } from 'react'

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CDataTable,
  CBadge,
  CSelect,
  CModal,
  CModalHeader,
  CModalFooter,
  CModalBody,
  CModalTitle,
  CSpinner
} from '@coreui/react'

import {
  CChartBar
} from '@coreui/react-chartjs'

import { Line } from 'react-chartjs-2';

import DataAPI from '../../../helpers/DataAPI.js'

import {DateFilter, formatDate, round, getDateLabel} from '../../../helpers/utils.js'

import ChartLoading from '../../others/ChartLoading.js'


const Charts = () => {

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState();
  const [curves, setCurves] = useState([]);
  const [dateRange, setDateRange] = useState('cm');
  const [generator, setGenerator] = useState('');
  const [filter, setFilter] = useState('');
  const [windSector, setWindSector] = useState('');
  const [generators, setGenerators] = useState([]);
  const [performance, setPerformance] = useState({});
  const [filtersSelected, setFiltersSelected] = useState(false);
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: []
  });


  useEffect(() => {
    loadGenerators();
  }, []);


  const fetchData = (options = {}) => {

    const {period, generator, filter, windSector} = options

    setLoading(true);

    const body = {}

    if (generator) body.generators = [generator];
    if (filter) body.filter = [filter];
    if (windSector) {
      body.angleFrom = windSector.split('-')[0]
      body.angleTo = windSector.split('-')[1]
    }
    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }


    DataAPI({
      'endpoint': 'chart/powerCurve',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

          const generatorData = response.generator && response.generator[0].data;

          setDataLoaded(true);
          setData(response);
          
      }
    );


  }


  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (!dataLoaded && response  && !response.error) {

          setGenerator(response.generators[0].id);
          setGenerators(response.generators);

        }
      }
    );


  }


  const refreshChart = (generatorData) => {

    const curves = generatorData.curves;

    if (curves.length==0) return;
        
      const dataset = {
        title: '',
        labels: [],
        label: "Reference Power Curve",
        backgroundColor: 'red',
        data: []
      };
      const dataset2 = {
        labels: [],
        label: "Turbine Power Curve",
        backgroundColor: 'blue',
        data: []
      };
      const labels = [];
      curves[0].x.forEach(
        x => {
          labels.push(round(x))
        }
      );

      dataset.data = curves[0].y;
      dataset2.data = curves[1].y;

      graphData.datasets.push(dataset);
      graphData.datasets.push(dataset2);

      setGraphData(graphData);


  }

  const filterSubmit = () => {

    setDataLoaded(false)
    setFiltersSelected(true);
    fetchData({generator: generator, period: dateRange, windSector: windSector})

  }


  const options = {
    animation: {
      duration: loading ? 0 : 1000
    },
    plugins: {
      legend: {
        display: false
      }
    }
  };


  return (
    <CCard>

    <CCardHeader>
          <CRow>
            <CCol sm="6">
              <h3 id="traffic" className="card-title mb-0">
                Power Curve - Analysis
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
            </CCol>
           </CRow>


        </CCardHeader>
      
      <CCardBody>

        <CRow className={"py-3 mb-4 mx-0 bg-light"} style={{borderRadius:"3px"}}>
          <CCol lg="9" md="9" className="">

          <CRow>
            <CCol xl="4" md="6" className="d-flex flex-center px-md-1 py-2 px-4">
                <h6 className="mr-2 ml-md-4 m-0 col-md-5 text-right col-4 px-0 px-sm-1" style={{minWidth:'64px'}}>Generator</h6>
                <CSelect disabled={loading} custom name="generator" id="generator" className="col-md-7 mr-2" onChange={(ev) => {setGenerator(ev.target.value);}}>
                    {generators.map((oGenerator) => (
                                      <option value={oGenerator.id}>
                                        {oGenerator.code}
                                      </option>
                                    ))}</CSelect>
            </CCol>
            <CCol xl="4" md="6" className="d-flex flex-center px-md-1 py-2 px-4">
                  <h6 className="mr-2 ml-md-4 m-0 col-md-5 text-right col-4 px-0 px-sm-1">Period</h6>
                  <div class="col-md-7 px-0 mr-2">
                    <DateFilter disabled={loading} value={dateRange} options={['cm','30d','cy','12w','12m','x','xx']} onChange={(value) => {setDateRange(value);}}/>
                  </div>
            </CCol>

            <CCol xl="4" md="6" className="d-flex flex-center px-md-1 py-2 px-4">
              <h6 className="mr-2 ml-md-4 m-0 col-md-5 text-right col-4 px-0 px-sm-1">Wind sector</h6>
              <CSelect disabled={loading} custom name="windSector" id="windSector" className="col-md-7 mr-2" onChange={(ev) => {setWindSector(ev.target.value);}}>
                <option value="" selected></option>
                <option value="0-45">0-45</option>
                <option value="45-90">45-90</option>
                <option value="90-135">90-135</option>
                <option value="135-180">135-180</option>
                <option value="180-225">180-225</option>
                <option value="225-270">225-270</option>
                <option value="270-315">270-315</option>
                <option value="315-360">315-360</option>
              </CSelect>
            </CCol>

            </CRow>

          </CCol>
          <CCol md="3" className="text-right d-flex flex-center flex-justify-end px-4 py-2">
            <CButton color="primary" className="mx-2" onClick={() => filterSubmit()} >Submit</CButton>
          </CCol>
        </CRow>
        
        { filtersSelected && 

        <CRow>
          <CCol sm="7" className="text-center">

          {dataLoaded || dataLoadError ? 

            <div>
              <div style={{marginBottom:'50px'}}>
                <div className="text-left" style={{width: '100%'}}>Power (KWh)</div>
                <Line data={graphData} options={options} />
                <div className="text-center mt-1" style={{width: '100%'}}>Wind speed (m/s)</div>
              </div>
            </div>

          : 
            <CSpinner 
              className="loading-spinner"
              color='#321fdb'
            />
          }
            
          </CCol>
        </CRow>


        }

      </CCardBody>



    </CCard>
  )
}

export default Charts
