    loadGenerators();
  }, []);

  const fetchData = (dateRange) => {

    const {period, generator} = options

    setLoading(true);

    const body = {}

    if (generator) body.generators = [generator];

    // console.log('.....', period, period.split('-').length)

    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }

    
    DataAPI({
      'endpoint': 'chart/pitchWindspeed',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

          // if (!response.generator) return(alert('No generator datasets'));


          const generatorData = response.generator && response.generator[0].data;

          setDataLoaded(true);
          setData(response);
          

          if(generatorData && generatorData.hasOwnProperty('curves')){
            setCurves(generatorData.curves);
            refreshChart(generatorData.curves);
          } else {
            setCurves([]);
          }

          if (generatorData){
            if(generatorData.hasOwnProperty('dataUse')) processTableData1(generatorData.dataUse);
            if(generatorData.hasOwnProperty('dataFilteredDueTo')) processTableData2(generatorData.dataFilteredDueTo);
            if(generatorData.hasOwnProperty('performance')) setPerformance(generatorData.performance);
          }
      }
    );


  }


  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (!dataLoaded && response  && !response.error) {

          setGenerator(response.generators[0].id);
          setGenerators(response.generators);

          fetchData({period: dateRange,  generator: response.generators[0].id});

        }
      }
    );


  }



  const refreshChart = (curves) => {


    if (curves.length==0) return;
    

      const graphData = {
        labels: [],
        datasets: [],
        backgroundColor: ["red", "blue", "green", "blue", "red", "blue"]
      }

    
      const dataset = {
        title: '',
        labels: [],
        label: "Reference Power Curve",
        backgroundColor: 'red',
        data: []
      };

      const dataset2 = {
        labels: [],
        label: "Turbine Power Curve",
        backgroundColor: 'blue',
        data: []
      };

      const dataset3 = {
        labels: [],
        label: "Time percentage",
        backgroundColor: 'blue',
        data: []
      };

      const labels = [];

      const labels2 = [];


      const graphData2 = {
        labels: [],
        datasets: [],
        backgroundColor: ["red", "blue", "green", "blue", "red", "blue"]
