      dataset2.data = curves[1].y;


      const max = Math.ceil(Math.max(...curves[2].x));
      const windSpeeds = Array(max - 1 + 1).fill().map((_, idx) => 1 + idx)
      const percentages = [];
      let aux;
      let qty;
      let percentage;
      const qtyTotal = curves[2].x.length;
      windSpeeds.forEach(
        function process(windSpeed) { 
          aux = curves[2].x.filter(x => x < windSpeed && x > windSpeed-1);
          qty = aux.length;
          percentage = (Math.round((qty*100/qtyTotal) * 100) / 100).toFixed(2);

          // percentage = (qty*100/qtyTotal) * 100 / 100;
          // percentage = +(Math.round(percentage + "e+2")  + "e-2");

          percentages.push(percentage);
          graphData2.labels.push(windSpeed-1 + " - " + windSpeed);
        }
      );

      dataset3.data = percentages;
*/

      graphData.datasets.push(dataset);
      graphData.datasets.push(dataset2);

      graphData.labels = labels;

      setGraphData(graphData);


}






  const dataSets = [];


    const parks = [
    {
        "id": 2,
        "code": "location #2",
        "name": "location sample",
        "address": "Location for sample",
        "latitude": -34.288322967923214,
        "longitude": -54.050271841125806,
        "outputCapacity": 3.0,
        "dataDefinitionId": 2,
        "dataDefinition": null,
        "stations": null,
        "generators": null
    }
]



  const dates = [
    'Hoy',
    'Ayer',
    'Esta semana',
    'Este mes',
    'Este año'
  ]


  // const data2 = {
  //   datasets: [
  //     {
  //       label: 'A dataset',
  //       data: [{x:4,y:55},{x:66,y:43}],
  //       backgroundColor: 'rgba(255, 99, 132, 1)',
  //     },
  //   ],

  // }


  // const change = (ev) => {
  // }

  // const refreshChart = (dateRange) => {
  //   fetchData(dateRange);
  // }

  const options = {animation: {duration: loading ? 0 : 1000}};

  return (
      <CCard className="mb-4">

      <CCardHeader>
          <CRow>
            <CCol sm="7">
              <h4 id="traffic" className="card-title mb-0">
                Diagnostics
              </h4>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
              </CCol>

               <CCol sm="5" class="text-right">

            <DateFilter value={dateRange} options={['cm','cy','12w','12m','x']} disabled={loading} onChange={(value) => { setDateRange(value); fetchData(value);}} />

                            </CCol>

            
                            </CRow>


        </CCardHeader>

        <CCardBody>
         
          {/* <Scatter data={data2} options={options} /> */}
          <Scatter data={graphData} options={options} />
        </CCardBody>

      </CCard>
  )
}

export default Charts
