import React, { useState, useEffect, useRef } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CAlert,
  CLabel,
  CButton,
  CDataTable,
  CInputCheckbox
} from '@coreui/react'

import DataAPI from '../../../helpers/DataAPI.js'


const Reports = () => {

  const [tableData, setTableData] = useState([]);
  const [configData, _setConfigData] = useState([]);
  const configDataRef = useRef(configData);
  const setConfigData = data => {
    configDataRef.current = data;
    _setConfigData(data);
  };
  const [tableFields, setTableFields] = useState([]);
  const [loaded, setLoaded] = useState(false);
  const [scopedSlots, setScopedSlots] = useState(false);
  const [reports, setReports] = useState([]);
  const [locations, setLocations] = useState([]);


  useEffect(() => {
    loadConfiguration();
  }, []);

  

  const loadConfiguration = () => {
    
    DataAPI({
      'endpoint': 'report/configure',
      'method': 'GET'
    }).then(
      response => {

        if (response.error) {
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        if (response.loaded) {
          
          setReports(response.reports)

          let locations = [];
          let rows = [];
          response.locations.forEach((location,index) => {
              locations[location.id] = location.name;
              rows[location.id] = [];
          });
          setLocations(locations)

          response.settings.forEach((setting,index) => {
            rows[setting.locId][setting.repId] = setting.selected;

          });

          let data = []


          let tableData = [];
          rows.map((row, locId) => {
            let rowData = [];
            row.map((setting, repId) => {
              rowData[repId] = setting;
            });
            rowData['windFarm'] = locations[locId];
            tableData.push(rowData);
            data[locId] = rowData;
          });


          setConfigData(data);
          

          let sSlots = {};
          let reports = [];
          response.reports.forEach((report,index) => {
            reports[report.id] = report.title;
            sSlots[report.id] = (item,x) => {
              if (item[report.id]) {
                return (
                <td>
                  <CInputCheckbox  name={"check-"+response.locations[x].id+"-"+report.id} className={'mx-0'} defaultChecked onChange={(ev)=>updateData(ev)}/>
                </td>)
              } else {
                return (
                <td>
                  <CInputCheckbox  name={"check-"+response.locations[x].id+"-"+report.id} className={'mx-0'} onChange={(ev)=>updateData(ev)}/>
                </td>)
              }
            }
          });
          setScopedSlots(sSlots);

          const tableFields = reports.map((title, id) => ({
              key: id,
              label: title
            }
          ));
          tableFields.unshift({
            key: 'windFarm',
            label: 'Wind Farm'
          });

          setTableData(tableData);
          setTableFields(tableFields);

          setLoaded(true);

        }

      }
    );


  }

const updateData = (ev) => {
  let name = ev.target.name;
  let nameSplit = name.split('-');
  let locId = nameSplit[1];
  let repId = nameSplit[2];
  let data = configDataRef.current;
  data[locId][repId] = ev.target.checked;
  setConfigData(data);
}

const saveChanges = () => {

  const body = {}

  configDataRef.current.forEach((row,locId) => {
    row.forEach((setting,repId) => {
      body.settings.push({
        "locId": locId,
        "repId": 2,
        "selected": setting
      })
    })
  })
  body.settings;

    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    
    DataAPI({
      'endpoint': 'report/configure',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }
      })
      

}



  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="6">
            <h3 id="traffic" className="card-title mb-0">
              Reports
            </h3>
            <div className="text-medium-emphasis">Settings</div>
          </CCol>
        </CRow>
      </CCardHeader>
      <CCardBody>
        <CRow>
          <CCol sm="6">
            
            <CLabel className={'mb-5'} >Please select reports you wish to receive by e-mail</CLabel>
            {/* {parksRows} */}
            {loaded &&
              <div>
                <CDataTable
                      items={tableData}
                      fields={tableFields}
                      striped
                      scopedSlots = {scopedSlots}
                    />
                <CRow>
                  <CCol xs="12">
                    <CButton onClick={saveChanges} color="primary" className="px-4 mr-3">Save</CButton>
                  </CCol>
                </CRow>
              </div>
            }

          </CCol>
        </CRow>

      </CCardBody>
    </CCard>
  )
}

export default Reports
