import React, { useEffect, useState } from 'react'
import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, getDateLabel, formatDate, formatDate2, round} from '../../../helpers/utils.js'

import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CSpinner,
  CLabel,
} from '@coreui/react'

import {
  CChartLine,
  CChartPolarArea
} from '@coreui/react-chartjs'

import { Line } from 'react-chartjs-2';

import { DocsLink } from 'src/reusable'

const ClimateTrends = () => {

  const [dateRange, setDateRange] = useState('y');
  const [groupBy, setGroupBy] = useState('hour');
  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [selectedGenerators, setSelectedGenerators] = useState([]);
  const [filterSubmitted, setFilterSubmitted] = useState(false);
  const [generators, setGenerators] = useState([]);
  const [generatorColors, setGeneratorColors] = useState([]);
  const [generatorsSelected, setGeneratorsSelected] = useState(false);
  const [metMastSelected, setMetMastSelected] = useState(true);
  const [allSelected, setAllSelected] = useState(true);

  const [lineChartOneData, setLineChartOneData] = useState({
    labels: [],
    datasets: []
  });
  const [lineChartTwoData, setLineChartTwoData] = useState({
    labels: [],
    datasets: []
  });
  const [lineChartThreeData, setLineChartThreeData] = useState({
    labels: [],
    datasets: []
  });
  const [lineChartFourData, setLineChartFourData] = useState({
    labels: [],
    datasets: []
  });

  const colors =  ['#003f5c', '#7a5195', '#bc5090', '#ef5675', '#ff764a', '#ffa600','#9ceb01']


  useEffect(() => {
    loadGenerators();
  }, []);
  

  const fetchData = (period) => {

    setLoading(true);

    const body = {}

    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy;
    body.generators = selectedGenerators;
    
    DataAPI(
      {
      'endpoint': 'chart/climate',
      'method': 'POST',
      'body': body
    })
      .then(function (responseData) {

        if (responseData.error && responseData.error.message) {
          setDataLoadError(true)
          alert(responseData.error.message)
        } 

        setLoading(false);

        if (responseData && responseData.graphs) {

          const labels = responseData.graphs[0].data.map( (x,i) => {
            return x.datetimeFrom.split(' ')[0];
          })
          const graphData1 = {
            labels: labels,
            datasets: []
          };
          const graphData2 = {
            labels: labels,
            datasets: []
          };
          const graphData3 = {
            labels: labels,
            datasets: []
          };
          const graphData4 = {
            labels: labels,
            datasets: []
          };


          if (metMastSelected) {
            const datasetMetMastDefault = {
              label: 'Met Mast Average',
              borderColor: '#f32e2e',
              pointBackgroundColor: 'transparent',
              pointBorderColor: 'transparent',
              backgroundColor: 'transparent',
            }
            const dataset1MetMast = {
              ...datasetMetMastDefault,
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmWindSpeed;
                      }),
            }
            const dataset2MetMast = {
              label: 'Met Mast Average',
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmTemperature;
                      }),
              backgroundColor: '#f32e2e',
            }
            const dataset3MetMast = {
              label: 'Met Mast Average',
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmAirDensity;
                      }),
              backgroundColor: '#f32e2e',
            }
            const dataset4MetMast = {
              label: 'Met Mast Average',
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmWindDirection;
                      }),
              backgroundColor: '#f32e2e',
            }
            const dataset5MetMast = {
              label: 'Met Mast Average',
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.mmTurbulenceIntensity;
                      }),
              backgroundColor: '#f32e2e',
            }
            graphData1.datasets.push(dataset1MetMast)
            graphData2.datasets.push(dataset2MetMast)
            graphData3.datasets.push(dataset3MetMast)
            graphData4.datasets.push(dataset4MetMast)
            graphData5.datasets.push(dataset5MetMast)
          }

          if (allSelected) {
            const dataset1All = {
              label: 'Turbine Average',
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.taWindSpeed;
                      }),
              backgroundColor: '#222222',
            }
            const dataset2All = {
              label: 'Turbine Average',
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.taTemperature;
                      }),
              backgroundColor: '#222222',
            }
            const dataset4All = {
              label: 'Turbine Average',
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.taWindDirection;
                      }),
              backgroundColor: '#222222',
            }
            const dataset5All = {
              label: 'Turbine Average',
              data: responseData.graphs[0].data.map( (x,i) => {
                        return x.taTurbulenceIntensity;
                      }),
              backgroundColor: '#222222',
            }
            graphData1.datasets.push(dataset1All)
            graphData2.datasets.push(dataset2All)
            graphData4.datasets.push(dataset4All)
            graphData5.datasets.push(dataset5All)
          }


          const metMastWindSpeedDataSet = {
            label: 'Met Mast',
            borderColor: '#f32e2e',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: []
          };
          const turbinesAverageWindSpeedDataSet = {
            label: 'Turbines Average',
            borderColor: '#930303',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: []
          };
          const metMastTemperatureDataSet = {
            label: 'Met Mast Temperature',
            borderColor: '#f32e2e',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            yAxisID: 'yTemperature',
            data: []
          };
          const turbinesAverageTemperatureDataSet = {
            label: 'Turbines Average Temperature',
            borderColor: '#930303',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            yAxisID: 'yTemperature',
            data: []
          };
          const metMastAirDensityDataSet = {
            labels: [],
            label: "Met Mast Air Density",
            borderColor: '#068bff',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            yAxisID: 'yAirDensity',
            data: []
          };
          const metMastWindDirectionDataSet = {
            labels: [],
            label: "Met Mast",
            borderColor: '#f32e2e',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: []
          };
          const turbinesAverageWindDirectionDataSet = {
            labels: [],
            label: "Turbines Average",
            borderColor: '#930303',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: []
          };
          const metMastTurbulenceIntensityDataSet = {
            labels: [],
            label: "Met Mast",
            borderColor: '#f32e2e',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: []
          };
          const turbinesAverageTurbulenceIntensityDataSet = {
            labels: [],
            label: "Turbines Average",
            borderColor: '#930303',
            pointBackgroundColor: 'transparent',
            pointBorderColor: 'transparent',
            backgroundColor: 'transparent',
            data: []
          };


          responseData.graphs.find((item) => {
            item.data.forEach((rDs) => {
              const mmWindSpeed = (Math.round(rDs.mmWindSpeed * 100) / 100).toFixed(2);
              metMastWindSpeedDataSet.data.push(mmWindSpeed);
              const taWindSpeed = (Math.round(rDs.taWindSpeed * 100) / 100).toFixed(2);
              turbinesAverageWindSpeedDataSet.data.push(taWindSpeed);

              metMastTemperatureDataSet.data.push(rDs.mmTemperature);
              turbinesAverageTemperatureDataSet.data.push(rDs.taTemperature);
              metMastAirDensityDataSet.data.push(rDs.mmAirDensity);
              metMastWindDirectionDataSet.data.push(rDs.mmWindDirection);
              turbinesAverageWindDirectionDataSet.data.push(rDs.taWindDirection);
              // metMastTurbulenceIntensityDataSet.data.push(rDs.mmWindDirection);
              turbinesAverageTurbulenceIntensityDataSet.data.push(rDs.taTurbulenceIntensity);

              let date = rDs.datetimeFrom.split(' ')[0];
              let time = rDs.datetimeFrom.split(' ')[1];
              let timeArr = time.split(':');

              const rDfrom = `${date} - ${timeArr[0]}:${timeArr[1]}`;
              const rDto = rDs.datetimeTo;
              windSpeedData.labels.push(rDfrom);
              temperatureData.labels.push(rDfrom);
              windDirectionData.labels.push(rDfrom);
              turbulenceIntensityData.labels.push(rDfrom);
            });

            windSpeedData.datasets.push(metMastWindSpeedDataSet);
            windSpeedData.datasets.push(turbinesAverageWindSpeedDataSet);
            temperatureData.datasets.push(metMastTemperatureDataSet);
            temperatureData.datasets.push(turbinesAverageTemperatureDataSet);
            temperatureData.datasets.push(metMastAirDensityDataSet);
            windDirectionData.datasets.push(metMastWindDirectionDataSet);
            windDirectionData.datasets.push(turbinesAverageWindDirectionDataSet);
            // turbulenceIntensityData.datasets.push(metMastTurbulenceIntensityDataSet);
            turbulenceIntensityData.datasets.push(turbinesAverageTurbulenceIntensityDataSet);
            
            
            setLineChartOneData(windSpeedData)
            setLineChartTwoData(temperatureData)
            setLineChartThreeData(windDirectionData)
            setLineChartFourData(turbulenceIntensityData)

          });

        }
      });

  }

  const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (!dataLoaded && response  && !response.error) {

          setGenerators(response.generators);
          // setSelectedGenerators(response.generators.map((gen) => (gen.id)));

          let colorIndex = 0
          response.generators.forEach((gen) => {
            generatorColors[gen.code] = colors[colorIndex%colors.length];
            setGeneratorColors(generatorColors);
            colorIndex++
          });

          setGeneratorsLoaded(true);

        }
      }
    );

  }

  const selectGenerator = (id) => {

    let newSelected = selectedGenerators;
    newSelected = newSelected.includes(id)
                          ? newSelected.filter(i => i !== id) // remove item
                          : [ ...newSelected, id ]; // add item
    setSelectedGenerators(newSelected);
  }

  const filterGenerators = () => {

    setDataLoaded(false);
    setGeneratorsSelected(false);
    setFilterSubmitted(true);
    if (selectedGenerators.length > 0) {
      setLoading(true);
      setGeneratorsSelected(true);
      fetchData(dateRange);
    }

  }


  const options =  {
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: true
    }
  }

  const optionsTemperature =  {
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    stacked: false,
    scales: {
      yTemperature: {
        type: 'linear',
        display: true,
        position: 'left',
      },
      yAirDensity: {
        type: 'linear',
        display: true,
        position: 'right',
        grid: {
          drawOnChartArea: false, // only want the grid lines for one axis to show up
        },
      },
    },
    elements: {
      line: {
          tension: .4
      }
    },
  };


  return (

      <CCard>

      <CCardHeader>
          <CRow>
            <CCol sm="9">
              <h3 id="traffic" className="card-title mb-0">
                Climate
              </h3>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
            </CCol>

            <CCol sm="3" class="text-right d-flex flex-center">
              <CRow className={"w-100 justify-content-end"}>
                <CCol className="d-flex flex-center px-md-1 py-2 px-4">
                  <h6 className="mr-2 ml-4 m-0" style={{lineHeight:1.2}}>Period</h6>
                  <DateFilter options={'y,cm,cy,12w,12m,x,xx'.split(',')} disabled={loading} onChange={(value) => { setDateRange(value); fetchData(value);}} />
                </CCol>
              </CRow>
            </CCol>
          </CRow>

        </CCardHeader>

        <CCardBody>

          <CRow className={"py-3 mb-4 mx-0 bg-light"} style={{borderRadius:"3px"}}>
            <CCol sm="10" className={"d-flex "} >
              <h6 className="mx-2 my-2 pt-1" style={{lineHeight:1.2,minWidth:'110px'}}>Select generators:</h6>   
              {generatorsLoaded ? 
                <div>
                  <CButton 
                    style={{backgroundColor:'#f32e2e',color: 'white'}} 
                    className={(metMastSelected ? "selected" : "") + " btn-generator mx-1 my-1"}
                    onClick={() => setMetMastSelected(!metMastSelected)}
                    >
                    Met
                  </CButton>
                  <CButton 
                    style={{backgroundColor:'#222222',color: 'white'}} 
                    className={(allSelected ? "selected" : "") + " btn-generator mx-1 my-1"}
                    onClick={() => setAllSelected(!allSelected)}
                    >
                    ALL
                  </CButton>
                  { generators.map((gen, index) => (  
                    <CButton 
                      style={{backgroundColor:generatorColors[gen.code],color: 'white'}} 
                      className={(selectedGenerators.includes(gen.id) ? "selected" : "") + " btn-generator mx-1 my-1"}
                      onClick={() => selectGenerator(gen.id)} 
                      id={gen.id}
                      >
                        {gen.code}
                    </CButton>
                  )) }
                </div>
                :
                <CSpinner 
                  size="sm"
                  className="loading-spinner"
                  color='#321fdb'
                />
              }
            </CCol>
            <CCol sm="2" className="text-right d-flex flex-end flex-justify-end ">
              <CButton color="primary" className="mx-2 mb-1" onClick={() => filterGenerators()} >Submit</CButton>
            </CCol>
        </CRow>

        { generatorsSelected ?

          <div>

            {!loading || dataLoadError ? 

              <div>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Wind Speed</h4>
                    <div>m/s</div>
                    <CChartLine
                      datasets={lineChartOneData.datasets}
                      labels={lineChartOneData.labels}
                      options={options}
                    />
                  </CCol>
                </CRow>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom">Temperature and Air Density</h4>
                    <div>C</div>
                    <Line data={lineChartTwoData}
                          options={optionsTemperature} />
                  </CCol>
                </CRow>

                <CRow className={'mb-5'}>
                  <CCol xs="12">
                    <h4 className="pb-2 mb-4 border-bottom" >Wind Direction</h4>
                    <div>Degrees</div>
                    <CChartLine
                      datasets={lineChartThreeData.datasets}
                      labels={lineChartThreeData.labels}
                      options={options}
                    />
                  </CCol>
                  <CCol xs="12" className={'mt-4'}>
                    <CCard className={'pl-3 pr-2'} style={{maxWidth: '480px', marginLeft: 'auto', marginRight: 'auto'}}>
                      <CCardBody>
                        <CRow>
                          <CCol sm="6" xs="12">
                            <CLabel className={'w-100 mb-0'}>0-45 North to Northeast</CLabel>
                            <CLabel className={'w-100 mb-0'}>45-90 NorthEast to East</CLabel>
                            <CLabel className={'w-100 mb-0'}> 90-135 East to SouthEast</CLabel>
                            <CLabel className={'w-100 mb-0'}>135-180 SouthEast to South</CLabel>
                          </CCol>
                          <CCol sm="6" xs="12">
                            <CLabel className={'w-100 mb-0'}> 180-225 South to SouthWest</CLabel>
                            <CLabel className={'w-100 mb-0'}>225-270 SouthWest to West</CLabel>
                            <CLabel className={'w-100 mb-0'}> 270-315 West to NorthWest</CLabel>
                            <CLabel className={'w-100 mb-0'}> 315-360 NorthWest to North</CLabel>
                          </CCol>
                        </CRow>
                      </CCardBody>
                    </CCard>
                    
                  </CCol>
                </CRow>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Turbulence Intensity</h4>
                    <div>%</div>
                    <CChartLine
                      datasets={lineChartFourData.datasets}
                      labels={lineChartFourData.labels}
                      options={options}
                    />
                  </CCol>
                </CRow>

              </div>
            : 
              <div className='text-center'>
                <CSpinner 
                  className="loading-spinner"
                  color='#321fdb'
                />
              </div>
            }

          </div>

        :

        <div>
          { filterSubmitted &&
            <CRow>
              <CCol className="text-center">
                Select one or more generators
              </CCol>
            </CRow>
          }
        </div>

}
        </CCardBody>
      </CCard>

  )
}

export default ClimateTrends
