import React, { useState, useEffect, useRef } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CAlert,
  CLabel,
  CButton,
  CDataTable,
  CInputCheckbox
} from '@coreui/react'

import DataAPI from '../../../helpers/DataAPI.js'

const Reports = () => {

  const [loaded, setLoaded] = useState(false);
  const [reports, setReports] = useState([]);

  const [sending, setSending] = useState(false);
  const [sent, setSent] = useState(false);
  const [message, setMessage] = useState('');


  useEffect(() => {
    loadOptions();
  }, []);

  const loadOptions = () => {
    
    DataAPI({
      'endpoint': 'report/generate',
      'method': 'GET'
    }).then(
      response => {

        if (response.error) {
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

          setReports(response.reports)

          

          setLoaded(true);

      }
    );


  }

  const requestReport = () => {

    setSending(true);
  
    // const body = {}
  
    // let settings = [];
  
    // configDataRef.current.map((row,locId) => (
    //   row.map((setting,repId) => {
    //     settings.push(
    //     {
    //       "locId": locId,
    //       "repId": repId,
    //       "selected": setting
    //     })}
    //   )
    // ))
  
    // body.settings = settings;
    
      
    DataAPI({
      'endpoint': 'report/generate',
      'method': 'POST',
      'body': body
    }).then(
      response => {
  
        setSaving(false)
        setChangesMade(false)
  
        if (response.error) {
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }
  
        if (response.saved) {
          setSaved(true);
          setMessage('Your report has been sent')
        }
      })
        
  
  }
  

  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="6">
            <h3 id="traffic" className="card-title mb-0">
              Reports
            </h3>
            <div className="text-medium-emphasis">Generate</div>
          </CCol>
        </CRow>
      </CCardHeader>
      <CCardBody className={'px-md-5 pb-md-5 pt-md-4'}>
        <CRow>
          <CCol sm="6">
            { sended &&
              <CAlert color="success" >{message}</CAlert>
            }
            <CLabel className={'mb-4'} >Generate and Download Report</CLabel>
            <CRow>
              <CCol xs="12">
                <h6 className="mr-2 ml-4 mb-0" style={{lineHeight:2.4,minWidth:'64px'}}>Select report type</h6>
                <CSelect disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); }} custom name="generator" id="generator">
                  <option value="" disabled>-- Select --</option>
                {reports.map((reports) => (
                                    <option value={oGenerator.id}>
                                      {oGenerator.title}
                                    </option>
                                  ))}</CSelect>
              </CCol>
            </CRow>
            <CRow>
              <CCol xs="12">
                <CButton onClick={requestReport} color="primary" disabled={sending} className="px-4 mr-3">Submit</CButton>
              </CCol>
            </CRow>

          </CCol>
        </CRow>

      </CCardBody>
    </CCard>
  )
}

export default Reports
