import { Redirect } from "react-router-dom";


import DataAPI from '../../../helpers/DataAPI.js'
import {setCookie} from '../../../helpers/sessionCookie.js'

import React, {useState} from 'react'
import { Link } from 'react-router-dom'
import {
  CButton,
  CCard,
  CCardBody,
  CCardGroup,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CLink,
  CRow
} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import logo from '../../../assets/renovus-logo.png'

const ResetPassword = () => {

  const [email, setEmail] = useState('');

  const authenticateUser = () => {

    DataAPI({
      'endpoint': 'security/authenticate',
      'method' : 'POST',
      'body': {
          "email": username,
          "password": password
      },
    }).then(
      response => {


        if (response.authenticated) {
          console.log('response~~~~~~',response)
          
          setCookie('name', response.name);
          setCookie('client', response.client.id && response.client.id);
          setCookie('location', response.location.id);
          
          setAuthenticated(true);
          window.location.reload();
        } else {
          alert('wrong user!');
        }

      }
    ).catch(
      e=> { alert('error!') }
    )

  }

  const resetPassword = () => {

  }

  return (


    authenticated ?

      <Redirect to={'/'} />:

  
    
    <div className="bg-primary c-app c-default-layout flex-row align-items-center">
      <CContainer>
        <CRow className="justify-content-center">
          <CCol md="8">
            <CCardGroup>
              <CCard className="p-4 border-light">
                <CCardBody>
                  <CForm>
                    <h1 className="text-dark-blue">Reset Password</h1>
                    <p className="text-muted">Enter your e-mail address</p>
                    <CInputGroup className="mb-3">
                      <CInputGroupPrepend>
                        <CInputGroupText>
                          <CIcon name="cil-user" />
                        </CInputGroupText>
                      </CInputGroupPrepend>
                      <CInput onChange={(ev) => {setEmail(ev.target.value)}} value={username} type="text" placeholder="E-mail" autoComplete="username" />
                    </CInputGroup>
                    <CRow>
                      <CCol xs="12">
                        <CButton onClick={sendResetPassword} color="primary" className="px-4 mr-3">Send</CButton>
                      </CCol>
                    </CRow>
                  </CForm>
                </CCardBody>
              </CCard>
              <CCard className="text-dark bg-light border-light py-5 d-md-down-none" style={{ width: '44%' }}>
                <CCardBody className="text-center">
                  <div>
                    <img src={logo} width="250" class="mb-3"/>
                    <p>Please login with your e-mail and password.</p>
                  </div>
                </CCardBody>
              </CCard>
            </CCardGroup>
          </CCol>
        </CRow>
      </CContainer>
    </div>
    
  )


}

export default Login
