import React, {useEffect, useState} from 'react'

import {
  CBadge,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CRow,
  CSelect,
  CSpinner
} from '@coreui/react'

import {
  CChartBar,
  CChartLine,
  CChartDoughnut,
  CChartRadar,
  CChartPie,
  CChartPolarArea
} from '@coreui/react-chartjs'

import { DocsLink } from 'src/reusable'

import DataAPI from '../../../helpers/DataAPI.js'

import usersData from '../../users/UsersData'

import {formatDate, DateFilter, getDateLabel, round} from '../../../helpers/utils.js'

import ChartLoading from '../../others/ChartLoading.js'

const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}

const fields = [
  'from',
  'to',
  'metMastWindSpeed',
  'temperature',
  'metMastWindDirection',
  'turbineStatus',
  'turbineAlarmCode',
  'turbineWindDirection',
  'turbineWindPower',
  'turbineWindSpeed'
]




const Charts = () => {

  const [data, setData] = useState([]);
  const [tableData, setTableData] = useState([]);
  
  const [dateRange, setDateRange] = useState('y');
  const [groupBy, setGroupBy] = useState('week');
  const [generators, setGenerators] = useState([]);
  const [generator, setGenerator] = useState("WTG32");

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [generatorsLoaded, setGeneratorsLoaded] = useState(false);
  const [loading, setLoading] = useState(false);



   const fetchData = (options = {}) => {

    const {period, groupBy, generator} = options

    setLoading(true);


    const body = {}

    if (generator && generator !== '0') body.generators = [generator];

    if (period && period.split('--').length==2) {
      body.from = period.split('--')[0]
      body.to = period.split('--')[1]
    } else {
      body.period = period
    }
    body.groupBy = groupBy

DataAPI({
      'endpoint': 'chart/dataAvailability',
      'body': body,
      'method': 'POST'
    }).then(
      response => {

        setLoading(false);


        if (response.error) {
          setDataLoadError(true);
           if (response.error.message) {
            return(alert(response.error.message))
           } else {
            return(alert(response.error)) 
           }
        }

          const tableData = [];
        if (response && response.data){

          response.data.table.forEach(
            row => {
              tableData.push({
                from: formatDate(row.from),
                to: formatDate(row.to),
                metMastWindDirection: round(row.metMast.direction)+'%',
                metMastWindSpeed: round(row.metMast.speed)+'%',
                temperature: round(row.metMast.temperature)+'%',
                turbineAlarmCode: round(row.turbineState.alarm)+'%',
                turbineStatus: round(row.turbineState.state)+'%',
                turbineWindDirection: round(row.windAverages.direction)+'%',
                turbineWindPower: round(row.windAverages.powerAverage)+'%',
                turbineWindSpeed: round(row.windAverages.speed)+'%',
              })
            }
          );

          setTableData(tableData);
          setDataLoaded(true);
        }
      }
    ).catch(
      e => {
        setLoading(false);
        alert(e);

      }
    );

   }


   useEffect(() => {
    loadGenerators();
  }, []);


   const loadGenerators = () => {

    DataAPI({
      'endpoint': 'admin/locations/current',
      'method': 'GET'
    }).then(
      response => {
        
        if (!dataLoaded && response  && !response.error) {

          setGenerator(response.generators[0].id);
          setGenerators(response.generators);

          fetchData({period: dateRange, groupBy: groupBy, generator: response.generators[0].id});

        }
      }
    );


  }

  return (
          <>
          

      <CRow>

        <CCol xs="12" lg="12">
          <CCard>
            


      <CCardHeader>
          <CRow>
            <CCol sm="6">
              <h4 id="traffic" className="card-title mb-0">
                Data Availability
              </h4>
              <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div>
              </CCol>
            
            <CCol sm="2" class="text-right d-flex">
              <div class="col-sm-6 px-2">
                <h6 style={{lineHeight:2.4}}>Group by</h6>
              </div>
              <div class="col-sm-6 px-2">
            <CSelect disabled={loading} onChange={(ev) => {setGenerator(ev.target.value); fetchData({period: dateRange, groupBy: groupBy, generator: ev.target.value});}} custom name="ccmonth" id="ccmonth">

              <option value="0">All generators</option>

            {generators.map((oGenerator) => (
                                <option value={oGenerator.id}>
                                  {oGenerator.code}
                                </option>
                              ))}</CSelect>
                              </div>
            </CCol>

            <CCol sm="2" class="text-right d-flex">
                <CSelect value={groupBy} disabled={loading} onChange={(ev) => { setGroupBy(ev.target.value); fetchData({period: dateRange, groupBy: ev.target.value, generator: generator});}} custom name="ccmonth" id="ccmonth">
                  <option value="day">Day</option>
                  <option value="week" selected>Week</option>
                  <option value="month">Month</option>
                </CSelect>
              </CCol>

            <CCol sm="2" class="text-right d-flex">
              <DateFilter value={dateRange} options={['y','cm','cy','12w','12m','x']} disabled={loading} onChange={(value) => { setDateRange(value); fetchData({generator: generator, period: value, groupBy: groupBy });}} />
            </CCol>
                            </CRow>


        </CCardHeader>

            <CCardBody>

            <div style={{textAlign: 'right'}}>

            {dataLoaded || dataLoadError
                    ? 
                  <CDataTable
                    items={tableData}
                    fields={fields}
                    striped
                    itemsPerPage={50}
                    pagination
                    scopedSlots = {{
                      'status':
                        (item)=>(
                          <td>
                            <CBadge color={getBadge(item.status)}>
                              {item.status}
                            </CBadge>
                          </td>
                        )

                    }}
                  />
                  : 
                  <ChartLoading/>
            }
            </div>
            </CCardBody>
          </CCard>
        </CCol>
      </CRow>

    </>
  )
}

export default Charts
