import React, { useState, useEffect } from 'react'
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCardHeader,
  CRow,
  CCol,
  CButton,
  CButtonGroup,
  CProgress,
  CCardFooter,
  CFormSelect,
  CDataTable,
  CBadge,
  CSelect,
  CModal,
  CModalHeader,
  CModalFooter,
  CModalBody,
  CModalTitle,
  CSpinner
} from '@coreui/react'
import { DocsLink } from 'src/reusable'

import DataAPI from '../../../helpers/DataAPI.js'
import {DateFilter, formatDate, formatDate2, round, getDateLabel, formatNumber} from '../../../helpers/utils.js'
import {getCookie} from '../../../helpers/sessionCookie.js'
import { Bar } from 'react-chartjs-2';

const Budget = () => {

  const currentYear = new Date().getFullYear()
  const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

  const [dataLoaded, setDataLoaded] = useState(false);
  const [dataLoadError, setDataLoadError] = useState(false);
  const [loading, setLoading] = useState(false);
  const [fiscalYear, setFiscalYear] = useState(currentYear);
  const [addEstimatedCurtailments, setAddEstimatedCurtailments] = useState(true);
  const [parks, setParks] = useState([]);
  const [parksLoaded, setParksLoaded] = useState(false);
  const [park, setPark] = useState(null);
  const [difference1, setDifference1] = useState([]);
  const [difference2, setDifference2] = useState([]);
  const [cumulativeDifference1, setCumulativeDifference1] = useState([]);
  const [cumulativeDifference2, setCumulativeDifference2] = useState([]);
  const [graphData1, setGraphData1] = useState({
    labels: [],
    datasets: []
  });
  const [graphData2, setGraphData2] = useState({
    labels: [],
    datasets: []
  });


  useEffect(() => {
    fetchParks();
  });


  const fetchParks = () => {

    DataAPI({
      'endpoint': 'security/authenticate/location',
      'method': 'GET'
    }).then(
      response => {
        
        if (!parksLoaded) {
          setParks(response);
          setParksLoaded(true);
          setPark(response[0].id)
        }

      }
    );

  }


  const fetchData = () => {

    setLoading(true);

    const body = {}
    body.locIds = [park];
    body.fiscalYear = fiscalYear
    body.addEstimatedCurtailments = addEstimatedCurtailments

    DataAPI({
      'endpoint': 'chart/budget',
      'method': 'POST',
      'body': body
    }).then(
      response => {


        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        const budgetData = response.budgetData;

        setDataLoaded(true);

        if (budgetData.length==0) return;

        const graphData = {
          labels: budgetData.map((x, i) => { 
            const startDate = new Date(x.startDate);
            return months[startDate.getMonth()]; 
          }),
          datasets: [
            {
              label: budgetData[0].estimates[0].label,
              data: budgetData.map((x, i) => { return x.estimates[0].estimateMwh }),
              borderColor: '#7a5195',
              backgroundColor: '#7a5195',
              order: 1
            },
            {
              label: budgetData[0].estimates[1].label,
              data: budgetData.map((x, i) => { return x.estimates[1].estimateMwh }),
              borderColor: '#bc5090',
              backgroundColor: '#bc5090',
              order: 1
            },
            {
              label: 'Actual Production',
              data: budgetData.map((x, i) => { return x.actualProductionMwh }),
              borderColor: '#ffa600',
              backgroundColor: '#ffa600',
              type: 'line',
              order: 0
            },
          ],
        }

        const dif1 = budgetData.map((x, i) => { return x.estimates[0].actualVsEstimate })
        const dif2 = budgetData.map((x, i) => { return x.estimates[1].actualVsEstimate })
        setDifference1(dif1)
        setDifference2(dif2)

        setGraphData1(graphData);

      }
    );


    DataAPI({
      'endpoint': 'chart/budgetCumulative',
      'method': 'POST',
      'body': body
    }).then(
      response => {

        setLoading(false);

        if (response.error) {
          setDataLoadError(true);
          if (response.error.message) {
            return(alert(response.error.message))
          } else {
            return(alert(response.error)) 
          }
        }

        const budgetData = response.budgetData;

        setDataLoaded(true);

        if (budgetData.length==0) return;

        const graphData = {
          labels: budgetData.map((x, i) => { 
            const startDate = new Date(x.startDate);
            return months[startDate.getMonth()]; 
          }),
          datasets: [
            {
              label: budgetData[0].estimates[0].label,
              data: budgetData.map((x, i) => { return x.estimates[0].estimateMwh }),
              borderColor: '#7a5195',
              backgroundColor: '#7a5195',
              order: 1
            },
            {
              label: budgetData[0].estimates[1].label,
              data: budgetData.map((x, i) => { return x.estimates[1].estimateMwh }),
              borderColor: '#bc5090',
              backgroundColor: '#bc5090',
              order: 1
            },
            {
              label: 'Actual Production',
              data: budgetData.map((x, i) => { return x.actualProductionMwh }),
              borderColor: '#ffa600',
              backgroundColor: '#ffa600',
              type: 'line',
              order: 0
            },
          ],
        }

        const dif1 = budgetData.map((x, i) => { return x.estimates[0].actualVsEstimate })
        const dif2 = budgetData.map((x, i) => { return x.estimates[1].actualVsEstimate })
        setCumulativeDifference1(dif1)
        setCumulativeDifference2(dif2)

        setGraphData2(graphData);

      }
    );


  }

  
  const options = {
    responsive: true,
    animation: {duration: loading ? 0 : 1000},
    tooltips: {
      enabled: true
    }
  };

  const optionsGraph1 =  {
    ...options,
    plugins: {
      tooltip: {
        // mode: 'index',
        callbacks: {
          label: function(tooltipItem, data) {
            if (tooltipItem.dataset.label === "Actual Production")  {
              return [
                tooltipItem.dataset.label + ': ' + round(tooltipItem.raw,1) + 'MWh',
                "% Difference vs. " + tooltipItem.dataset[1].label + ":" + difference1[tooltipItem.index],
                "% Difference vs. " + tooltipItem.dataset[2].label + ":" + difference2[tooltipItem.index]
            
              ];
            }
            let unit = tooltipItem.dataset.label === "Average Wind Speed" ? ' m/s' : ' %'
              
          }
        }
      }
    }
  };

  const optionsGraph2 =  {
    ...options,
    plugins: {
      tooltip: {
        // mode: 'index',
        callbacks: {
          label: function(tooltipItem, data) {
            if (tooltipItem.dataset.label === "Actual Production")  {
              return [
                tooltipItem.dataset.label + ': ' + round(tooltipItem.raw,1) + 'MWh',
                "% Difference vs. " + tooltipItem.dataset[1].label + ":" + cumulativeDifference1[tooltipItem.index],
                "% Difference vs. " + tooltipItem.dataset[2].label + ":" + cumulativeDifference2[tooltipItem.index]
            
              ];
            }
            let unit = tooltipItem.dataset.label === "Average Wind Speed" ? ' m/s' : ' %'
              
          }
        }
      }
    }
  };


  return (
    <CCard>
      <CCardHeader>
        <CRow>
          <CCol sm="4">
            <h3 id="traffic" className="card-title mb-0">
              Budget
            </h3>
            {/* <div className="small text-medium-emphasis">{getDateLabel(dateRange)}</div> */}
          </CCol>

          <CCol sm="8" className="text-right d-flex px-2 flex-center">
            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'64px'}}>Fiscal Year</h6>
            <CSelect disabled={loading} custom name="fiscalYear" id="fiscalYear" className="" onChange={(ev) => {setFiscalYear(ev.target.value);}}>
              {[0,1,2,3,4].map((x) => (
                                <option value={currentYear-x}>
                                  {currentYear-x}
                                </option>
                              ))}
            </CSelect>

            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4,minWidth:'170px'}}>Add Estimated Curtailments</h6>
            <CSelect disabled={loading} custom defaultValue={"1"} name="addEstimatedCurtailments" id="addEstimatedCurtailments" className="" onChange={(ev) => {setAddEstimatedCurtailments(ev.target.value);}}>
              <option value="1">Yes</option>
              <option value="0">No</option>
            </CSelect>

            <h6 className="mr-2 ml-4 m-0" style={{lineHeight:2.4}}>Park</h6>
            <CSelect disabled={loading} defaultValue={park} custom name="park" id="park" className="" onChange={(ev) => {setPark(ev.target.value);}}>
                {parks && parks.map((park) => (
                    <option key={park.id} value={park.id}>
                      {park.name}
                    </option>
                      ))}
            </CSelect>

            <CButton color="primary" disabled={loading || !parksLoaded} className="mr-2 ml-3" onClick={() => { fetchData();}} >Submit</CButton>
          </CCol>

          </CRow>
      </CCardHeader>
      <CCardBody>

        { (loading || dataLoaded) &&
          <div>

            { !loading || dataLoadError ? 
              <div>
                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Monthly Production</h4>
                    <div>MWh</div>
                    <Bar 
                      data={graphData1}
                      options={optionsGraph1} 
                    />
                  </CCol>
                </CRow>

                <CRow className={'mb-5'}>
                  <CCol>
                    <h4 className="pb-2 mb-4 border-bottom" >Cumulative Production</h4>
                    <div>MWh</div>
                    <Bar 
                      data={graphData2}
                      options={optionsGraph2} 
                    />
                  </CCol>
                </CRow>

              </div>
            : 
              <div className='text-center'>
                <CSpinner 
                  className="loading-spinner"
                  color='#321fdb'
                />
              </div>
            }
          </div>
        }


      </CCardBody>
    </CCard>
  )
}

export default Budget
